mapper.select:
select SQL_CALC_FOUND_ROWS p.*, w.id w_id, w.descrim w_descrim, w.name w_name, w.nick w_nick, w.birth w_birth, w.gender w_gender from ( select p.id pid, sum(r.tf) * log(b.post_cnt / (count(p.id) + 1)) importance from T_tag t, t_tgt_tag r, T_reply p, t_bulitine_board b WHERE t.word IN
#{word}
AND t.id = r.tag_id AND r.tgt_name = 'T_reply' AND r.tgt_id = p.id AND p.bb_id = b.id AND p.bb_id = #{boardId} group by p.id ) pst, T_reply p LEFT OUTER JOIN T_party w ON p.writer_id = w.id where pst.pid = p.id and w.descrim = 'user' order by pst.importance desc limit #{paging.limit} offset #{paging.offset};

SELECT count(1) = 1
  FROM T_reply r
 WHERE r.id = #{id};

select r.*, w.id w_id, w.descrim w_descrim, w.name w_name, w.nick w_nick, w.birth w_birth, w.gender w_gender from T_reply r LEFT OUTER JOIN T_party w ON r.writer_id = w.id WHERE genre LIKE CONCAT('%', #{genre}, '%')
and age_limit IN
#{ageLimit}
and w.descrim = 'user';

select r.*, w.id w_id, w.descrim w_descrim, w.name w_name, w.nick w_nick, w.birth w_birth, w.gender w_gender from T_reply r LEFT OUTER JOIN T_party w ON r.writer_id = w.id WHERE genre LIKE CONCAT('%', #{genre}, '%')
and age_limit IN
#{ageLimit}
and title LIKE CONCAT('%', #{search}, '%')
and w.descrim = 'user';

mapper.update:
update T_reply
   set TITLE = #{title}, 
   	   content = #{content},
	   genre = #{genre},
	   age_limit = #{ageLimit}
 where id = #{id}

update T_reply
   set content = #{content},
	   star_Score = #{starScore}
 where id = #{id}
